mapper.update:
update ssafy_member
set userpwd = #{userpwd}, email = #{email} , username =#{username}
where userid = #{userid}

mapper.select:
select userid, username, email
from ssafy_member
where userid = #{userid}

select username, userid
from ssafy_member
where userid= #{userid} and userpwd = #{userpwd}

select salt
from ssafy_member
where userid = #{userid}
